import React from 'react';
import Button from '@material-ui/core/Button';
import { useHistory } from "react-router-dom";
import authService from "../../Services/authService";

function Acceuil(props) {
  const history = useHistory();

  /* useEffect(() => {
    if (!(props?.user)) {
      history.push("/");
    }
  }); */
  console.log(props);
  const handleLogout = () => {
    authService.logout();
    history.push("/");
    window.location.reload();
  };

  return (
    <div className="container" style={{ minWidth: "100%", maxHeight: "100%" }}>
      <div className="row">
        <div className="col-12">
          {props?.role === 1 && <h1>hello admin: {props?.user.email}</h1>}
          {props?.role === 2 && <h1>hello guichet droit arabe: {props?.user.email}</h1>}
          {props?.role === 3 && <h1>hello guichet droit francais: {props?.user.email}</h1>}
          {props?.role === 4 && <h1>hello guichet economie: {props?.user.email}</h1>}
          {props?.role === 5 && <h1>hello service de diplomes: {props?.user.email}</h1>}
          {props?.role === 6 && <h1>hello decanat: {props?.user.email}</h1>}
          {props?.role === 7 && <h1>hello bureau d'ordre: {props?.user.email}</h1>}
          {props?.role === 8 && <h1>hello guichet de retrait: {props?.user.email}</h1>}
        </div>
        <Button variant="contained"
          color="primary" 
          onClick={handleLogout}
        >
          Se d√©connecter
        </Button>
      </div>
    </div>
  );
}

export default Acceuil;
