import React, { useEffect, useState } from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import EditUserForm from './EditUserFrom';






import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
      maxWidth: "80%",
      marginTop: theme.spacing(8),
      marginBottom: theme.spacing(8),
      backgroundColor: 'rgba(255,255,255,0.8)',
      
    },
    groupButtons :{
        marginBottom: theme.spacing(1),
        marginLeft: theme.spacing(75)
        
    },
    button: {
        margin: theme.spacing(1),
      },
    container: {
        marginTop: theme.spacing(20),
        
    },
}));

const columns = [
    { field: 'id', headerName: 'ID', width: 120 },
    {
        field: 'email',
        headerName: 'Email',
        width: 300,
        editable: false,
    },
    {
        field: 'role',
        headerName: 'Role',
        width: 200,
        editable: false,
    },
    
];





export default function UsersGrid() {
    const classes = useStyles();
    const [id, setId] = useState([]);
    const [users, setUsers] = useState([]);
    const [buttonStatus,setButtonStatus]=useState(true);
    const [open, setOpen] = useState(false);
    const [userEdit, setUserEdit] = useState([]);


    const handleRowSelection = (e) => {
        setId(e);
        setButtonStatus(false);
        
        
    }
    
    const handleEdit= () => {
        setOpen(true);
        axios.get("http://127.0.0.1:8000/api/users/"+id)
            .then((response) => {
                setUserEdit(response.data.user);
            }).catch(err => {
                console.log(err)
            });

    }

    console.log(userEdit);
    const handleDelete= (e) => {
        e.preventDefault();
        axios.delete("http://127.0.0.1:8000/api/users/" + id)
            .then((response) =>{
                console.log(response);
            }).catch((err) =>{
                console.log(err)
            })
        
    };

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/users")
            .then((response) => {
                setUsers(response.data.users);
                // console.log(response.data.users);


            }).catch(err => {
                console.log(err)
            })
            {open ? <EditUserForm handleOpen={open} user={userEdit} /> : <div></div> }


    }, [open]);
    return (
    <Container  >
        <Grid
            className={classes.container}
            container
            direction="row"  
            justifyContent="center"
            alignItems="center"
           
            >
                   
                        
                        <div className={classes.groupButtons}>
                            <Button variant="contained" color="primary" size="small"
                            className={classes.button} startIcon={<DeleteIcon />} 
                            disabled={buttonStatus} onClick={handleEdit}>Editer</Button>

                            <Button variant="contained" color="secondary" size="small"
                            className={classes.button} startIcon={<EditIcon />} 
                            disabled={buttonStatus} onClick={handleDelete}>Supprimer</Button>
                        </div>
                            
                        
                   
            <div style={{ width: '70%' }}>
                <DataGrid
                    rows={users}
                    columns={columns}
                    pageSize={5}
                    checkboxSelection={false}
                    disableSelectionOnClick={false}
                    autoHeight={true}
                    NoRowsOverlay
                    onSelectionModelChange={handleRowSelection}
                    disableMultipleSelection={true}
                    
                />
                
            </div>
        </Grid>
        
       
        </Container>
    );
}
