import React, { useState} from "react";
import axios from "axios";
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from "@material-ui/core/styles";
import TextField from '@material-ui/core/TextField';
import img from "../../img.jpg";



const useStyles =  makeStyles((theme) => ({
    root: {
        
      },
    image: {
        backgroundImage: `url(${img})`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: theme.spacing(80),
      },
      input: {
        width: "80%",
      },
    
    }));


function Login() {
    const classes = useStyles();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");


    const handleEmail =(e) =>{
        setEmail(e.target.value);
        
    };
    const handlePassword =(e) =>{
        setPassword(e.target.value);
        
    };
    const handleLogin = (e) =>{
    
        let config = {
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
                "Access-Control-Allow-Headers" : "Content-Type",
                "Access-Control-Allow-Origin":"*",
                },
          }
        e.preventDefault();
        console.log('Login');
        axios.post("https://localhost:8000/login", {email,password},config)
        .then((response) => {
            console.log(response)
        }).catch(err =>{
            console.log(err)
        })

        

};
    return (
        <Grid
            container spacing={4}
            direction="row"
            justifyContent="center"
            alignItems="center"
            >

            <Grid container item xs={12} sm={6} className={classes.image}/>
            <Grid xs={12} sm={6} component={Paper} elevation={3} square >
                <div>hello</div>
                <form onSubmit={handleLogin}>
                <TextField
                    value={email}
                    onChange={handleEmail}
                    variant="outlined"
                    margin="normal"
                    size="medium"
                    id="email"
                    required={true}
                    label="Username Or Email"
                    name="email"
                    autoComplete="email"
                    autoFocus

                /><br/><br/>
                <TextField  label="Password" variant="outlined" onChange={handlePassword} />
            
            
        </form>

            </Grid>
        </Grid>



        
    );

}

export default Login;
