import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Message from './Message';


const API_URL = "http://127.0.0.1:8000/api/";



function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}


const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '40ch',
        },
    },
}));

export default function Form(props) {
    const classes = useStyles();
    const [lien, setLien] = useState('');
    const [api_id, setApiId] = useState('');
    const [type_formulaire, setTypeFormulaire] = useState('');
    const [success, setSuccess] = useState(false);
    const [errors, setErrors] = useState(false);
    const [message, setMessage] = useState('');


    useEffect(() => {
        setTypeFormulaire(props.typeFormulaire);


    }, [])


    const handleLien = (event) => {
        setLien(event.target.value);
    };

    const handleApiId = (event) => {
        setApiId(event.target.value);
    };


    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post(API_URL + "formulaires",
            {
                type_formulaire,
                lien,
                api_id

            }).then((response) => {
                console.log(response);
                setSuccess(true);
                setMessage(response.data.message);
            }).catch(err => {
                console.log(err.response);
                setErrors(true);
                setMessage("Une erreur est survenu, veuillez r√©essayer..");
            })



    }


    return (
        <div>
            {success && <Message message={message} success="success"/>}
            {errors && <Message message={message} success="error"/>}

            
            <form className={classes.root} onSubmit={handleSubmit}>
                <Typography gutterBottom variant="h5" component="h2" color="primary">
                    {type_formulaire}
                </Typography>
                <div>
                    <TextField
                        id="outlined-name"
                        label="Lien du formulaire GOOGLE FORM"
                        value={props.form.lien}
                        onChange={handleLien}
                        variant="outlined"
                        required
                    />
                    <TextField
                        id="outlined-uncontrolled"
                        label="API ID du formulaire"
                        onChange={handleApiId}
                        value={props.form.api_id}
                        variant="outlined"
                        required={true}
                    />
                </div>
                <Button type='submit' variant="contained" color="primary"
                    startIcon={<AddBoxRoundedIcon />}
                >Ajouter</Button>

            </form>
        </div>

    );
}
