import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';
    


const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '40ch',
        },
    },
}));

export default function Form(props) {
    const classes = useStyles();
    const [link, setLink] = useState('');
    const [apiId, setApiId] = useState('');
    const [typeFormulaire, setTypeFormulaire] = useState('');


    useEffect(() => {
        setTypeFormulaire(props.typeFormulaire);


    })


    const handleLink = (event) => {
        setLink(event.target.value);
    };

    const handleApiId = (event) => {
        setApiId(event.target.value);
    };

    return (
        <form className={classes.root} noValidate autoComplete="off">
            <Typography gutterBottom variant="h5" component="h2" color="primary">
                {typeFormulaire}
            </Typography>
            <div>
                <TextField
                    id="outlined-name"
                    label="Lien du formulaire GOOGLE FORM"
                    value={link}
                    onChange={handleLink}
                    variant="outlined"
                />
                <TextField
                    id="outlined-uncontrolled"
                    label="API ID du formulaire"
                    onChange={handleApiId}
                    value={apiId}
                    variant="outlined"
                />

                <Button type='submit' variant="contained" color="primary" size="small"
                    className={classes.fab} startIcon={<AddBoxRoundedIcon />}
                >
                    Ajouter</Button>
            </div>
        </form>
    );
}
