import React, { useState, useEffect, initialState } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Login from "./Components/Login/Login";
import Acceuil from "./Components/Interfaces/Admin/Acceuil";

function App() {
  const [user, setUser] = useState(initialState);
  const [role, setRole] = useState(initialState);

  useEffect(() => {
    async function getUserData() {
      await userService.getUserData().then((response) => {
        setUser(response?.data);
        setRole(response?.data?.roles[0]?.id);
      }, (error) => {
        setUser(null)
      });
    }
    getUserData();
  }, [role]);

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/" component={Login} />
          <Route exact path="/Acceuil" component={Acceuil} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
