import React, { useState, useEffect, initialState } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Login from "./Components/Login/Login";
import Acceuil from "./Components/Interfaces/Acceuil";
import Paperbase from "./Components/Demandes/Paperbase";
import userService from "./Services/userService";

function App() {
  const [user, setUser] = useState(initialState);
  const [role, setRole] = useState(initialState);

  useEffect(() => {
    async function getUserData() {
      await userService.getUserData().then((response) => {
        setUser(response?.data);
        setRole(response?.data?.roles[0]?.id);
      }, (error) => {
        setUser(null)
      });
    }
    getUserData();
  }, []);

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/" component={() => (<Login user={user} />)}/>
          {user === null ? 
          (<> <Route component={() => (<Login user={user} />)}/> </>) 
          : 
          (<> <Route exact path="/acceuil" component={() => (<Acceuil user={user} role={role} />)}/> 
              <Route exact path="/demandes" component={() => (<Paperbase user={user}/>)}/>
          </>)}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
