import React ,{useEffect, useState} from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import EditIcon from '@material-ui/icons/Edit';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import ProfilInfos from './ProfilInfos';
import axios from 'axios';


const API_URL = "http://127.0.0.1:8000/api/";


const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        marginTop: theme.spacing(15),
        width: "60%",

    },
    cover: {
        width: 220,
        backgroundImage: 'url(https://source.unsplash.com/random)',
         marginRight: theme.spacing(2),

    },
    h5: {
        marginRight: theme.spacing(10),
    }

}));

export default function Profil() {
    const classes = useStyles();
    const [user, setUser]= useState('');
    
    useEffect(() => {
        axios.get(`${API_URL}users/16`)
        .then((response) => {
            setUser(response.data.user);
        }).catch(err => {
            // setMessage("Erreur de chargement , veuillez reessayer !");

        })

    },[])

    return (
        <Container>

            <Card className={classes.root}>

                <CardMedia
                    className={classes.cover}
                    image=""
                    title="Live from space album cover"
                />

                <div>
                    <CardContent >

                    </CardContent>
                    <div className={classes.controls}>
                        <ProfilInfos title="Email ou Username" data={user.email}/>
                        <ProfilInfos title="Mot de passe" data=""/>
                        <ProfilInfos title="Profil" data={user.role}/>
                    </div>
                </div>

            </Card>
        </Container>
    );
}
