import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';
import axios from 'axios';


const API_URL = "http://127.0.0.1:8000/api/";



const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '40ch',
        },
    },
}));

export default function Form(props) {
    const classes = useStyles();
    const [link, setLink] = useState('');
    const [apiId, setApiId] = useState('');
    const [typeFormulaire, setTypeFormulaire] = useState('');


    useEffect(() => {
        setTypeFormulaire(props.typeFormulaire);


    }, [])


    const handleLink = (event) => {
        setLink(event.target.value);
    };

    const handleApiId = (event) => {
        setApiId(event.target.value);
    };


    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post(API_URL + "formulaires",
            {
                typeFormulaire,
                link,
                apiId

            }).then((response) => {
                console.log(response);
                <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
                    <Alert onClose={handleClose} severity="success">
                        This is a success message!
                    </Alert>
                </Snackbar>
            }).catch(err => {
                console.log(err.response);
            })



    }
    return (
        <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit}>
            <Typography gutterBottom variant="h5" component="h2" color="primary">
                {typeFormulaire}
            </Typography>
            <div>
                <TextField
                    id="outlined-name"
                    label="Lien du formulaire GOOGLE FORM"
                    value={link}
                    onChange={handleLink}
                    variant="outlined"
                    required
                />
                <TextField
                    id="outlined-uncontrolled"
                    label="API ID du formulaire"
                    onChange={handleApiId}
                    value={apiId}
                    variant="outlined"
                    required
                />
            </div>
            <Button type='submit' variant="contained" color="primary"
                startIcon={<AddBoxRoundedIcon />}
            >Ajouter</Button>

        </form>
    );
}
