import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import InputAdornment from '@material-ui/core/InputAdornment';
import TextField from '@material-ui/core/TextField';
import img from "../../img.jpg";
import PersonIcon from '@material-ui/icons/Person';
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import Container from '@material-ui/core/Container';
import Fab from '@material-ui/core/Fab';
import EditIcon from '@material-ui/icons/Edit';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';

import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';


const API_URL = "http://127.0.0.1:8000/api/";


const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,

  },
  media: {
    height: 90,
    backgroundImage: 'url(https://source.unsplash.com/random)',
  },
  extendedIcon: {
    marginRight: theme.spacing(1),
  },
  fab: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(3),
    marginLeft: theme.spacing(10),
  },
  container: {
    marginTop: theme.spacing(5),
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',

  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
}));





export default function UserForm(props) {
  const classes = useStyles();
  const [roles, setRoles] = useState([]);
  const [open, setOpen] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  let [passwordConfirm, setPasswordConfirm] = useState('');
  const [errors, setErrors] = useState('');
  const [role, setRole] = useState('');
let profil=0;




  useEffect(() => {
    axios.get(API_URL + "roles")
      .then((response) => {
        setRoles(response.data.roles);
    }).catch(err => {
        console.log(err)
    })
    
    setOpen(props.handleOpen);
    console.log(props);
  }, []);

  // const handleClose=(e)=>{
      //     setOpen(false);
      // }
      
      const handleRole = (e) => {
        profil=e.target.value;
        console.log(profil);
      }
      

  const handleEmail = (e) => {
    setEmail(e.target.value);
    if (email.length < 8) {
      setErrors({ ...errors, email: "Minimum : 6 caractères" });
    }
     else {
      setErrors({ ...errors, email: null });
    }
  }

  const handlePassword = (e) => {
    setPassword(e.target.value);
    if (password?.length < 7){
      setErrors({...errors,password: "Le mot de passe doit dépasser 7 caractères"});
    } else {
      setErrors({ ...errors, password: null });
    }

  }



  const handlePasswordConfirm = (e) => {
    passwordConfirm=e.target.value;
    if(password !== passwordConfirm) {
      setErrors({ ...errors, passwordConfirm: "Les password doivent etre identiques"});
    } else{
      setErrors({ ...errors, passwordConfirm: null}); 
    }
  }

  const handleSubmit = (e) => {
    if (!Object.values(errors).some((x) => x !== null && x !== "")) {
    console.log('heeere submit');
    // if( password === passwordConfirm){
    axios.post(API_URL + "users",
      {
        email,
        password,
        role

      }).then((response) => {
        console.log(response);
        e.forceRefresh();
      }).catch(err => {
        console.log(err)
      })


    }

  }

  return (
    <div>

      <Dialog open={open} aria-labelledby="form-dialog-title">
        <DialogContent>

          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image=""
                title=""
              />
              <CardContent>

                <Typography gutterBottom variant="h5" component="h2" color="primary">
                  {props.title}
                </Typography>

              </CardContent>

            </CardActionArea>
            <CardActions>
              <Container component="main" maxWidth="xs">
                <form className={classes.form} onSubmit={handleSubmit} validate>
                  <TextField
                    onChange={handleEmail}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <PersonIcon />
                        </InputAdornment>
                      ),
                    }}
                    value={props.user ? props.user.email :email}
                    variant="outlined"
                    margin="normal"
                    required
                    fullWidth
                    size="small"
                    id="email"
                    label="Email"
                    name="email"
                    autoComplete="email"
                    autoFocus
                    error={Boolean(errors?.email)}
                    helperText={errors?.email}
                  />
                  <TextField
                    onChange={handlePassword}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <VpnKeyIcon />
                        </InputAdornment>
                      ),
                    }}
                    variant="outlined"
                    margin="normal"
                    required
                    fullWidth
                    size="small"
                    name="password"
                    placeholder="********"
                    label="Nouveau Password "
                    type="password"
                    id="password"
                    autoComplete="current-password"
                    error={Boolean(errors?.password)}
                    helperText={errors?.password}
                  />

                  <TextField
                    onChange={handlePasswordConfirm}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <VpnKeyIcon />
                        </InputAdornment>
                      ),
                    }}
                    variant="outlined"
                    margin="normal"
                    required
                    fullWidth
                    size="small"
                    name="password"
                    placeholder="********"
                    label="Confirmer Password "
                    type="password"
                    id="password"
                    autoComplete="current-password"
                    error={Boolean(errors?.passwordConfirm)}
                    helperText={errors?.passwordConfirm}
                  />


                  <TextField
                    id="standard-select-currency"
                    select
                    label="Profil"
                    onChange={handleRole}
                    value={props.user? props.user.roleId  : role}
                    helperText="Choisissz un profil"
                    variant="outlined"
                    margin="normal"
                    fullWidth
                    required
                    size="small"
                    error={Boolean(errors?.role)}
                    helperText={errors?.role}


                  >
                    {roles.map((role) => (
                      <MenuItem key={role.id} value={role.id}>
                        {role.name}
                      </MenuItem>
                    ))}
                  </TextField>
                    { props.typeForm === "add" ?
                        <Button type='submit' variant="contained" color="primary" size="small"
                            className={classes.fab} startIcon={<AddBoxRoundedIcon />}>
                            Ajouter</Button>
                        :
                        <Button
                            variant="contained" color="primary" size="small"
                            type='submit' className={classes.fab} startIcon={<EditIcon />} 
                            >Editer</Button>
                       }
                </form>
              </Container>
            </CardActions>



          </Card>
        </DialogContent>
      </Dialog>
    </div>
  );
}
