import React, { useState, useEffect, initialState } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Login from "./Components/Login/Login";
import Acceuil from "./Components/Interfaces/Acceuil";
import Paperbase from "./Components/Demandes/Paperbase";
import authService from "./Services/authService";

function App() {
  const [user, setUser] = useState(initialState);
  const [role, setRole] = useState(initialState);

  useEffect(() => {
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser?.user);
      setRole(foundUser.user?.roles[0]?.id);
    }
    console.log(JSON.parse(loggedInUser));
  }, []); // an empty to check if there's a logged in user the first time the app loads.
  
  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/" component={() => (<Login user={user} />)}/>
          { !user ? 
          (<> <Route render={() => (<Login user={user} />)}/> </>) 
          : 
          (<> <Route exact path="/Acceuil" component={() => (<Acceuil user={user} role={role} />)}/> 
              <Route exact path="/Demandes" component={() => (<Paperbase user={user} role={role} />)}/>
          </>)}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
