import React, {useEffect} from 'react';
import Button from '@material-ui/core/Button';
import { useHistory } from "react-router-dom";
import authService from "../../Services/authService";

function Acceuil(props) {
  const history = useHistory();

  useEffect(() => {
    if (!(props?.user)) {
      history.push("/");
    }
  });

  const handleLogout = () => {
    authService.logout().then(
      history.push("/"),
      window.location.reload()
    );
  };

  return (
    <div className="container" style={{ minWidth: "100%", maxHeight: "100%" }}>
      <div className="row">
        <div className="col-12">
          {props?.role === 1 && "hello admin: " && props?.user.email}
          {props?.role === 2 && "hello guichet droit arabe: " && props?.user.email}
          {props?.role === 3 && "hello guichet droit francais: " && props?.user.email}
          {props?.role === 4 && "hello guichet economie: " && props?.user.email}
          {props?.role === 5 && "hello service de diplomes: " && props?.user.email}
          {props?.role === 6 && "hello decanat: " && props?.user.email}
          {props?.role === 7 && "hello bureau d'ordre: " && props?.user.email}
          {props?.role === 8 && "hello guichet de retrait: " && props?.user.email}
        </div>
        <Button variant="contained"
          color="primary" 
          onClick={handleLogout}
        >
          Se d√©connecter
        </Button>
      </div>
    </div>
  );
}

export default Acceuil;
