export default function authHeaders() {
    const user = JSON.parse(localStorage.getItem("user"));

    if (user && user.accessToken) {
        //return { Authorization: "Bearer " + user.accessToken };
        return {
            headers: {
                'Authorization': `Bearer ${user.accessToken}`
            }
        };
    } else {
        return { 
            headers: { user }
        };
    }
}
