import React, { useEffect, useState } from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import EditUserForm from './EditUserFrom';
import AddUserForm from './AddUserForm';
import UserForm from './UserForm';
import axios from 'axios';
import swal from 'sweetalert';



const API_URL = "http://127.0.0.1:8000/api/";


const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "80%",
        marginTop: theme.spacing(8),
        marginBottom: theme.spacing(8),
        backgroundColor: 'rgba(255,255,255,0.8)',
        
    },
    groupButtons :{
        marginBottom: theme.spacing(1),
        marginLeft: theme.spacing(60)
        
    },
    button: {
        margin: theme.spacing(1),
    },
    container: {
        marginTop: theme.spacing(20),
        
    },
}));

const columns = [
    { field: 'id', headerName: 'ID', width: 120 },
    {
        field: 'email',
        headerName: 'Email',
        width: 300,
        editable: false,
        filterable: false,
    },
    {
        field: 'role',
        headerName: 'Role',
        width: 200,
        editable: false,
    },
    
];


export default function UsersGrid() {




    const classes = useStyles();
    const [id, setId] = useState('');
    const [users, setUsers] = useState([]);
    const [disableButton,setDisableButton]=useState(true);
    const [openAdd, setOpenAdd] = useState(false);
    const [openEdit, setOpenEdit] = useState(false);
    let [userEdit, setUserEdit] = useState([]);


    const handleRowSelection = (e) => {
        setId(e[0]);
        setDisableButton(false);
        
        
    }
    
    const handleEdit= () => {
        axios.get(API_URL+"users/"+id)
        .then((response) => {
            setUserEdit(response.data.user);
            console.log(userEdit);
            setOpenEdit(true);
        }).catch(err => {
            console.log(err)
        });
    }

    const handleAdd =()=>{ 
        setOpenAdd(true);

    }

    const handleDelete=  (e) => {
        swal({
            title: "Etes-vous sure ?",
            text: "Cette action est irreversible !",
            icon: "warning",
            buttons: true,
            dangerMode: true,
          })
          .then((willDelete) => {
            if (willDelete) {
                axios.delete(`${API_URL}users/${id}`)
                .then((response) =>{
                    console.log(response.data.message);
                    swal(response.data.message, {
                        icon: "success",
                      });
                }).catch((err) =>{
                    console.log(err)
                })
              
            } else {
            //   swal("Suppression annulÃ©e");
            }
          });
       
        
    };

    useEffect(() => {

        axios.get(`${API_URL}users`)
            .then((response) => {
                setUsers(response.data.users);
                // console.log(response.data.users);
            }).catch(err => {
                console.log(err)
            })

    }, []);
    return (
    <Container  >
        <Grid
            className={classes.container}
            container
            direction="row"  
            justifyContent="center"
            alignItems="center"
           
            >
                        
                            <Button variant="contained" color="primary" size="small"
                            className={classes.button} startIcon={<EditIcon />} 
                            disabled={disableButton} onClick={handleEdit}>Editer</Button>

                            <Button variant="contained" color="secondary" size="small"
                            className={classes.button} startIcon={<DeleteIcon />} 
                            disabled={disableButton} onClick={handleDelete}>Supprimer</Button>

                        <div className={classes.groupButtons}>
                        <Button variant="contained" color="secondary" size="small"
                            className={classes.button} startIcon={<AddBoxRoundedIcon />} 
                            onClick={handleAdd}>Ajouter</Button>      
                        </div>
                            
                        
                   
            <div style={{ width: '70%' }}>
                <DataGrid
                    rows={users}
                    columns={columns}
                    pageSize={5}
                    checkboxSelection={false}
                    disableSelectionOnClick={false}
                    autoHeight={true}
                    NoRowsOverlay
                    OnRowSelected={(row) => {
                        setId(row.data.id)}}
                    onSelectionModelChange={handleRowSelection}
                    disableMultipleSelection={true}
                    disableColumnMenu={true}
                    
                />
                
            </div>
        </Grid>
        
        {openEdit ? <UserForm handleOpen={openEdit} user={userEdit} title="Editer utilisateur" formType="edit"/> : <div></div> }
        {openAdd ? <UserForm handleOpen={openAdd} title="Ajouter utilisateur" formType="add"/> : <div></div> }
        </Container>
    ); 
}
