import  React ,{useEffect,useState} from 'react';
import { DataGrid } from '@material-ui/data-grid';
import axios from 'axios';

const columns = [
  { field: 'id', headerName: 'ID', width: 120 },
  {
    field: 'email ',
    headerName: 'Email',
    width: 300,
    editable: false,
  },
  {
    field: 'created_at ',
    headerName: 'cr',
    width: 300,
    editable: false,
  },
  {
    field: 'updated_at ',
    headerName: 'up',
    width: 300,
    editable: false,
  },
];





export default function Grid() {
    const [rows,setRows]=useState();
    useEffect(()=>{
        axios.get("http://127.0.0.1:8000/api/users")
        .then((response) => {
            setRows(user);
            console.log(response.data.users[0].roles);
            response.data.users.map((user)=>{
                
                user.roles.map((role)=>{
                    console.log(role);
                })
            });
        }).catch(err =>{
            console.log(err)
        })
    },[]);
  return (
    <div style={{ height: 400, width: '80%' }}>
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={5}
        checkboxSelection
        disableSelectionOnClick
      />
    </div>
  );
}
