import React, { useEffect, useState } from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import UserForm from './UserForm';
import axios from 'axios';
import swal from 'sweetalert';
import Alert from "@material-ui/lab/Alert";
import TextField from '@material-ui/core/TextField';
import SearchIcon from '@material-ui/icons/Search';
import MenuItem from '@material-ui/core/MenuItem';






const API_URL = "http://127.0.0.1:8000/api/";


const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "80%",
        marginTop: theme.spacing(8),
        marginBottom: theme.spacing(8),
        backgroundColor: 'rgba(255,255,255,0.8)',

    },
    groupButtons: {
        marginBottom: theme.spacing(1),
        marginLeft: theme.spacing(60),

    },
    button: {
        margin: theme.spacing(1),
    },
    container: {
        marginTop: theme.spacing(20),

    },
    margin: {
        marginTop: theme.spacing(5),


    },
}));

const columns = [
    { field: 'id', headerName: 'ID', width: 120 },
    {
        field: 'email',
        headerName: 'Email',
        width: 300,
        editable: false,
        filterable: false,
    },
    {
        field: 'role',
        headerName: 'Role',
        width: 200,
        editable: false,
    },

];


export default function UsersGrid() {




    const classes = useStyles();
    const [id, setId] = useState('');
    const [users, setUsers] = useState([]);
    const [disableButton, setDisableButton] = useState(true);
    const [openAdd, setOpenAdd] = useState(false);
    const [openEdit, setOpenEdit] = useState(false);
    let [userEdit, setUserEdit] = useState([]);
    const [message, setMessage] = useState('');
    let [searchItem, setSearchItem] = useState('');
    const [roles, setRoles] = useState([]);



    const handleRowSelection = (e) => {
        setId(e[0]);
        setDisableButton(false);


    }

    const handleEdit = () => {
        axios.get(API_URL + "users/" + id)
            .then((response) => {
                setUserEdit(response.data.user);
                console.log(userEdit);
                setOpenEdit(true);
            }).catch(err => {
                console.log(err)
            });
    }

    const handleAdd = () => {
        setOpenAdd(true);

    }

    const handleDelete = (e) => {
        swal({
            title: "Etes-vous sure ?",
            text: "Cette action est irreversible !",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((willDelete) => {
                if (willDelete) {
                    axios.delete(`${API_URL}users/${id}`)
                        .then((response) => {
                            console.log(response.data.message);
                            swal(response.data.message, {
                                icon: "success",
                            });
                        }).catch((err) => {
                            console.log(err)
                        })

                }
            });


    };

    const handleSearch = (e) => {
        setSearchItem(e.target.value);

    }

    const filterByRole = (role) => {
        
    }

    useEffect(() => {
        axios.get(`${API_URL}users/search/${searchItem}`)
            .then((response) => {
                setUsers(response.data.users);
            }).catch(err => {
                setMessage("Erreur de chargement , veuillez reessayer !");
            })

        axios.get(API_URL + "roles")
            .then((response) => {
                setRoles(response.data.roles);
            }).catch(err => {
                console.log(err)
            })

    }, [searchItem]);
    return (
        <Container  >
            {message && (
                <Alert
                    severity="error"
                    onClose={() => {
                        setMessage(null);
                    }}
                >
                    {message}
                </Alert>
            )}
            {/* search bar */}
            <div className={classes.margin}>
                <Grid container spacing={1} alignItems="flex-end">
                    <Grid item>
                        <SearchIcon />
                    </Grid>
                    <Grid item>
                        <TextField id="input-with-icon-grid" label="Recherche ..." onChange={handleSearch} />
                    </Grid>
                </Grid>
            </div>

            <div >
                    <TextField
                        id="standard-select-currency"
                        select
                        // label="Filter selon les roles"
                        onChange={filterByRole}
                        // value={role}
                        fullWidth
                        helperText="Filter selon les roles"
                        className={classes.margin}
                        variant="standard"
                        margin="normal"
                        size="small"
                    >
                        {roles.map((role) => (
                            <MenuItem key={role.id} value={role.id}>
                                {role.name}
                            </MenuItem>
                        ))}
                    </TextField>
            </div>

                <Grid
                    className={classes.container}
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="center"

                >

                {/* buttons */}
                <Button variant="contained" color="primary" size="small"
                    className={classes.button} startIcon={<EditIcon />}
                    disabled={disableButton} onClick={handleEdit}>Editer</Button>

                <Button variant="contained" color="secondary" size="small"
                    className={classes.button} startIcon={<DeleteIcon />}
                    disabled={disableButton} onClick={handleDelete}>Supprimer</Button>

                <div className={classes.groupButtons}>
                    <Button variant="contained" color="secondary" size="small"
                        className={classes.button} startIcon={<AddBoxRoundedIcon />}
                        onClick={handleAdd}>Ajouter</Button>
                </div>



                <div style={{ width: '70%' }}>
                    <DataGrid
                        rows={users}
                        columns={columns}
                        pageSize={5}
                        checkboxSelection={false}
                        disableSelectionOnClick={false}
                        autoHeight={true}
                        NoRowsOverlay
                        onSelectionModelChange={handleRowSelection}
                        disableMultipleSelection={true}
                        disableColumnMenu={true}

                    />

                </div>
            </Grid>

            {openEdit ? <UserForm handleOpen={openEdit} user={userEdit} title="Editer utilisateur" formType="edit" roles={roles} /> : <div></div>}
            {openAdd ? <UserForm handleOpen={openAdd} title="Ajouter utilisateur" formType="add" roles={roles} /> : <div></div>}
        </Container>
    );
}
