import React, { useEffect, useState } from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import AddBoxRoundedIcon from '@material-ui/icons/AddBoxRounded';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import UserForm from './UserForm';
import axios from 'axios';
import swal from 'sweetalert';
import Alert from "@material-ui/lab/Alert";
import TextField from '@material-ui/core/TextField';
import SearchIcon from '@material-ui/icons/Search';
import MenuItem from '@material-ui/core/MenuItem';
import InputAdornment from '@material-ui/core/InputAdornment';
import PersonOutlineIcon from '@material-ui/icons/PersonOutline';






const API_URL = "http://127.0.0.1:8000/api/";


const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: "80%",
        marginTop: theme.spacing(8),
        marginBottom: theme.spacing(8),
        backgroundColor: 'rgba(255,255,255,0.8)',

    },
    groupButtons: {
        marginBottom: theme.spacing(1),
        marginLeft: theme.spacing(60),

    },
    button: {
        margin: theme.spacing(1),
    },
    container: {
        marginTop: theme.spacing(20),

    },
    margin: {
        marginTop: theme.spacing(5),


    },
    select : {
        marginLeft : theme.spacing(90),
        marginBottom : theme.spacing(20)
      

    }
}));


///Data grid columns
const columns = [
    { field: 'id', headerName: 'ID', width: 120 },
    {
        field: 'email',
        headerName: 'Email',
        width: 300,
        editable: false,
        filterable: false,
    },
    {
        field: 'role',
        headerName: 'Role',
        width: 200,
        editable: false,
    },

];


export default function UsersGrid() {

/////States
    const classes = useStyles();
    const [id, setId] = useState('');
    const [users, setUsers] = useState([]);
    const [disableButton, setDisableButton] = useState(true);
    const [openAdd, setOpenAdd] = useState(false);
    const [openEdit, setOpenEdit] = useState(false);
    let [userEdit, setUserEdit] = useState([]);
    const [message, setMessage] = useState('');
    let [searchItem, setSearchItem] = useState('');
    const [roles, setRoles] = useState([]);
    const [roleFilter, setRoleFilter] = useState(0);
    const [deleteUser, setDeleteUser] = useState(false);

    //////////////////////////////////////////////////////////////
    useEffect(() => {
        axios.get(`${API_URL}users/search/${searchItem}`)
            .then((response) => {
                setUsers(response.data.users);
            }).catch(err => {
                setMessage("Erreur de chargement , veuillez reessayer !");
               
            })

        axios.get(API_URL + "roles")
            .then((response) => {
                setRoles(response.data.roles);
            }).catch(err => {
                console.log(err)
            })

        if(roleFilter !==0){
            axios.get(`${API_URL}users/role/${roleFilter}`)
                .then((response) => {
                    setUsers(response.data.users);
                }).catch(err => {
                    setMessage("Erreur de chargement , veuillez reessayer !");
                   
                })
        }

    }, [roleFilter,searchItem,  deleteUser]);
  
/////////////////////////////////////////////////////////
    const handleRowSelection = (e) => {
        setId(e[0]);
        setDisableButton(false);
    }

/////Open Edit Form with user data
    const handleEdit = () => {
        axios.get(API_URL + "users/" + id)
            .then((response) => {
                setUserEdit(response.data.user);
                console.log(userEdit);
                setOpenEdit(true);
            }).catch(err => {
                console.log(err)
            });
    }

/////Open add form
    const handleAdd = () => {
        setOpenAdd(true);

    }

/////Delete user
    const handleDelete = (e) => {
        swal({
            title: "Etes-vous sure ?",
            text: "Cette action est irreversible !",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((willDelete) => {
                if (willDelete) {
                    axios.delete(`${API_URL}users/${id}`)
                        .then((response) => {
                            console.log(response.data.message);
                            setDeleteUser(true);
                            swal(response.data.message, {
                                icon: "success",
                            });
                        }).catch((err) => {
                            console.log(err)
                        })

                }
            });


    };

    const handleSearch = (e) => {
        setSearchItem(e.target.value);

    }

    const filterByRole = (e) => {
        setRoleFilter(e.target.value);

    }
  return (
        <Container  >
            {message && (
                <Alert
                    severity="error"
                    onClose={() => {
                        setMessage(null);
                    }}
                >
                    {message}</Alert>
            )}

           </Container>
  );
}
